# 一般設定の翻訳
UPDATE configuration SET configuration_title = 'Store Name', configuration_description = 'The name of my store' WHERE configuration_key = 'STORE_NAME';
UPDATE configuration SET configuration_title = 'Store Owner', configuration_description = 'The name of my store owner' WHERE configuration_key = 'STORE_OWNER';
UPDATE configuration SET configuration_title = 'Telephone - Customer Service', configuration_description = 'Enter a telephone number for customers to reach your Customer Service department. This number may be sent as part of payment transaction details.' WHERE configuration_key = 'STORE_TELEPHONE_CUSTSERVICE';
UPDATE configuration SET configuration_title = 'Country', configuration_description = 'The country my store is located in <br /><br /><strong>Note: Please remember to update the store zone.</strong>' WHERE configuration_key = 'STORE_COUNTRY';
UPDATE configuration SET configuration_title = 'Zone', configuration_description = 'The zone my store is located in' WHERE configuration_key = 'STORE_ZONE';
UPDATE configuration SET configuration_title = 'Expected Sort Order', configuration_description = 'This is the sort order used in the expected products box.' WHERE configuration_key = 'EXPECTED_PRODUCTS_SORT';
UPDATE configuration SET configuration_title = 'Expected Sort Field', configuration_description = 'The column to sort by in the expected products box.' WHERE configuration_key = 'EXPECTED_PRODUCTS_FIELD';
UPDATE configuration SET configuration_title = 'Switch To Default Language Currency', configuration_description = 'Automatically switch to the language\'s currency when it is changed' WHERE configuration_key = 'USE_DEFAULT_LANGUAGE_CURRENCY';
UPDATE configuration SET configuration_title = 'Language Selector', configuration_description = 'Should the default language be based on the Store preferences, or the customer\'s browser settings?<br /><br />Default: Store\'s default settings' WHERE configuration_key = 'LANGUAGE_DEFAULT_SELECTOR';
UPDATE configuration SET configuration_title = 'Use Search-Engine Safe URLs (still in development)', configuration_description = 'Use search-engine safe urls for all site links' WHERE configuration_key = 'SEARCH_ENGINE_FRIENDLY_URLS';
UPDATE configuration SET configuration_title = 'Display Cart After Adding Product', configuration_description = 'Display the shopping cart after adding a product (or return back to their origin)' WHERE configuration_key = 'DISPLAY_CART';
UPDATE configuration SET configuration_title = 'Default Search Operator', configuration_description = 'Default search operators' WHERE configuration_key = 'ADVANCED_SEARCH_DEFAULT_OPERATOR';
UPDATE configuration SET configuration_title = 'Include meta-tags in product search?', configuration_description = 'Should a product\'s meta-tag keywords and meta-tag descriptions be considered in any <code>advanced_search_results</code> displayed?' WHERE configuration_key = 'ADVANCED_SEARCH_INCLUDE_METATAGS';
UPDATE configuration SET configuration_title = 'Store Address and Phone', configuration_description = 'This is the Store Name, Address and Phone used on printable documents and displayed online' WHERE configuration_key = 'STORE_NAME_ADDRESS';
UPDATE configuration SET configuration_title = 'Show Category Counts', configuration_description = 'Count recursively how many products are in each category' WHERE configuration_key = 'SHOW_COUNTS';
UPDATE configuration SET configuration_title = 'Tax Decimal Places', configuration_description = 'Pad the tax value this amount of decimal places' WHERE configuration_key = 'TAX_DECIMAL_PLACES';
UPDATE configuration SET configuration_title = 'Display Prices with Tax', configuration_description = 'Display prices with tax included (true) or add the tax at the end (false)' WHERE configuration_key = 'DISPLAY_PRICE_WITH_TAX';
UPDATE configuration SET configuration_title = 'Display Prices with Tax in Admin', configuration_description = 'Display prices with tax included (true) or add the tax at the end (false) in Admin(Invoices)' WHERE configuration_key = 'DISPLAY_PRICE_WITH_TAX_ADMIN';
UPDATE configuration SET configuration_title = 'Basis of Product Tax', configuration_description = 'On what basis is Product Tax calculated. Options are<br />Shipping - Based on customers Shipping Address<br />Billing Based on customers Billing address<br />Store - Based on Store address if Billing/Shipping Zone equals Store zone' WHERE configuration_key = 'STORE_PRODUCT_TAX_BASIS';
UPDATE configuration SET configuration_title = 'Basis of Shipping Tax', configuration_description = 'On what basis is Shipping Tax calculated. Options are<br />Shipping - Based on customers Shipping Address<br />Billing Based on customers Billing address<br />Store - Based on Store address if Billing/Shipping Zone equals Store zone - Can be overridden by correctly written Shipping Module' WHERE configuration_key = 'STORE_SHIPPING_TAX_BASIS';
UPDATE configuration SET configuration_title = 'Sales Tax Display Status', configuration_description = 'Always show Sales Tax even when amount is $0.00?<br />0= Off<br />1= On' WHERE configuration_key = 'STORE_TAX_DISPLAY_STATUS';
UPDATE configuration SET configuration_title = 'Show Split Tax Lines', configuration_description = 'If multiple tax rates apply, show each rate as a separate line at checkout' WHERE configuration_key = 'SHOW_SPLIT_TAX_CHECKOUT';
UPDATE configuration SET configuration_title = 'Wholesale Pricing', configuration_description = 'Should <em>Wholesale Pricing</em> be enabled for your site?  Choose <b>false</b> (the default) if you don\'t want that feature enabled. Otherwise, choose <b>Tax Exempt</b> to enable with tax-exemptions for all wholesale customers or <b>Pricing Only</b> to apply tax as usual for wholesale customers.' WHERE configuration_key = 'WHOLESALE_PRICING_CONFIG';
UPDATE configuration SET configuration_title = 'MFA Multi-Factor Authentication Required', configuration_description = '2-Factor authentication for Admin users' WHERE configuration_key = 'MFA_ENABLED';
UPDATE configuration SET configuration_title = 'PA-DSS Admin Session Timeout Enforced?', configuration_description = 'PA-DSS Compliance requires that any Admin login sessions expire after 15 minutes of inactivity. <strong>Disabling this makes your site NON-COMPLIANT with PA-DSS rules, thus invalidating any certification.</strong>' WHERE configuration_key = 'PADSS_ADMIN_SESSION_TIMEOUT_ENFORCED';
UPDATE configuration SET configuration_title = 'PA-DSS Strong Password Rules Enforced?', configuration_description = 'PA-DSS Compliance requires that admin passwords must be changed after 90 days and cannot re-use the last 4 passwords. <strong>Disabling this makes your site NON-COMPLIANT with PA-DSS rules, thus invalidating any certification.</strong>' WHERE configuration_key = 'PADSS_PWD_EXPIRY_ENFORCED';
UPDATE configuration SET configuration_title = 'PA-DSS Ajax Checkout?', configuration_description = 'PA-DSS Compliance requires that for some inbuilt payment methods, that we use ajax to draw the checkout confirmation screen. While this will only happen if one of those payment methods is actually present, some people may want the traditional checkout flow <strong>Disabling this makes your site NON-COMPLIANT with PA-DSS rules, thus invalidating any certification.</strong>' WHERE configuration_key = 'PADSS_AJAX_CHECKOUT';
UPDATE configuration SET configuration_title = 'Customer <em>Place Order</em>: Single Admin ID', configuration_description = 'Identify the ID number of an admin that is permitted to use the <em>Place Order</em> feature on the customers list, regardless of their assigned admin-profile. Set the value to 0 to disable the <em>Single Admin ID</em> feature.' WHERE configuration_key = 'EMP_LOGIN_ADMIN_ID';
UPDATE configuration SET configuration_title = 'Customer <em>Place Order</em>: Passwordless Login', configuration_description = 'Login directly to store without entering credentials' WHERE configuration_key = 'EMP_LOGIN_AUTOMATIC';
UPDATE configuration SET configuration_title = 'Customer <em>Place Order</em>: Admin Profiles', configuration_description = 'Identify the admin <em>User Profile IDs</em> that are permitted to use the <em>Place Order</em> feature on the customers list &mdash; all admins that are in these profiles are permitted. Enter the value as a comma-separated list (intervening blanks are OK) of Admin Profile IDs, e.g. <b>1, 2, 3</b>. Set the value to 0 to disable the <em>Admin Profiles</em> feature.<br><br><b>Default: 0 </b>' WHERE configuration_key = 'EMP_LOGIN_ADMIN_PROFILE_ID';
UPDATE configuration SET configuration_title = 'Admin Session Time Out in Seconds', configuration_description = 'Enter the time in seconds.<br />Max allowed is 900 for PCI Compliance Reasons.<br /> Default=900<br />Example: 900= 15 min <br /><br />Note: Too few seconds can result in timeout issues when adding/editing products' WHERE configuration_key = 'SESSION_TIMEOUT_ADMIN';
UPDATE configuration SET configuration_title = 'Admin Set max_execution_time for processes', configuration_description = 'Enter the time in seconds for how long the max_execution_time of processes should be. Default=60<br />Example: 60= 1 minute<br /><br />Note: Changing the time limit is only needed if you are having problems with the execution time of a process' WHERE configuration_key = 'GLOBAL_SET_TIME_LIMIT';
UPDATE configuration SET configuration_title = 'Show if version update available', configuration_description = 'Automatically check to see if a new version of Zen Cart is available. Enabling this can sometimes slow down the loading of Admin pages. (Displayed on main Index page after login, and Server Info page.)' WHERE configuration_key = 'SHOW_VERSION_UPDATE_IN_HEADER';
UPDATE configuration SET configuration_title = 'Store Status', configuration_description = 'What is your Store Status<br />0= Normal Store<br />1= Showcase no prices<br />2= Showcase with prices' WHERE configuration_key = 'STORE_STATUS';
UPDATE configuration SET configuration_title = 'Server Uptime', configuration_description = 'Displaying Server uptime can cause entries in error logs on some servers. (true = Display, false = don\'t display)' WHERE configuration_key = 'DISPLAY_SERVER_UPTIME';
UPDATE configuration SET configuration_title = 'Missing Page Check', configuration_description = 'Zen Cart can check for missing pages in the URL and redirect to Index page. For debugging you may want to turn this off. <br /><br /><strong>Default=On</strong><br />On = Send missing pages to \'index\'<br />Off = Don\'t check for missing pages<br />Page Not Found = display the Page-Not-Found page' WHERE configuration_key = 'MISSING_PAGE_CHECK';
UPDATE configuration SET configuration_title = 'cURL Proxy Status', configuration_description = 'Does your host require that you use a proxy for cURL communication?' WHERE configuration_key = 'CURL_PROXY_REQUIRED';
UPDATE configuration SET configuration_title = 'cURL Proxy Address', configuration_description = 'If you have a hosting service that requires use of a proxy to talk to external sites via cURL, enter their proxy address here.<br />format: address:port<br />ie: 127.0.0.1:3128' WHERE configuration_key = 'CURL_PROXY_SERVER_DETAILS';
UPDATE configuration SET configuration_title = 'HTML Editor', configuration_description = 'Please select the HTML/Rich-Text editor you wish to use for composing Admin-related emails, newsletters, and product descriptions' WHERE configuration_key = 'HTML_EDITOR_PREFERENCE';
UPDATE configuration SET configuration_title = 'Default for Notify Customer on Order Status Update?', configuration_description = 'Set the default email behavior on status update to Send Email, Do Not Send Email, or Hide Update.' WHERE configuration_key = 'NOTIFY_CUSTOMER_DEFAULT';
UPDATE configuration SET configuration_title = 'Show Category Counts - Admin', configuration_description = 'Show Category Counts in Admin?' WHERE configuration_key = 'SHOW_COUNTS_ADMIN';
UPDATE configuration SET configuration_title = 'Show linked status for categories', configuration_description = 'Show Category products linked status?' WHERE configuration_key = 'SHOW_CATEGORY_PRODUCTS_LINKED_STATUS';
UPDATE configuration SET configuration_title = 'Currency Conversion Ratio', configuration_description = 'When auto-updating currencies, what \"uplift\" ratio should be used to calculate the exchange rate used by your store?<br />ie: the bank rate is obtained from the currency-exchange servers; how much extra do you want to charge in order to make up the difference between the bank rate and the consumer rate?<br /><br /><strong>Default: 1.05 </strong><br />This will cause the published bank rate to be multiplied by 1.05 to set the currency rates in your store.' WHERE configuration_key = 'CURRENCY_UPLIFT_RATIO';
UPDATE configuration SET configuration_title = 'Currency Exchange Rate: Primary Source', configuration_description = 'Where to request external currency updates from (Primary source)<br><br>Additional sources can be installed via plugins.' WHERE configuration_key = 'CURRENCY_SERVER_PRIMARY';
UPDATE configuration SET configuration_title = 'Currency Exchange Rate: Secondary Source', configuration_description = 'Where to request external currency updates from (Secondary source)<br><br>Additional sources can be installed via plugins.' WHERE configuration_key = 'CURRENCY_SERVER_BACKUP';
