UPDATE configuration SET configuration_title = 'Disabled Product Status for Search Engines', configuration_description = 'When a product is marked Disabled (status=0) but is not deleted from the database, should Search Engines still show it as Available?<br>eg:<br>True = Return HTTP 200 response<br>False = Return HTTP 410<br>(Deleting it will return HTTP 404)<br><b>Default: false</b>' WHERE configuration_key = 'DISABLED_PRODUCTS_TRIGGER_HTTP200';
