UPDATE configuration SET configuration_title = 'Enable disabled product by available date', configuration_description = 'How should disabled products with a future available date be made active when the date is reached?<br />' WHERE configuration_key = 'ENABLE_DISABLED_UPCOMING_PRODUCT';
