UPDATE configuration SET configuration_title = 'Show Shopping Cart - Delete Checkboxes or Delete Button', configuration_description = 'Show on Shopping Cart Delete Button and/or Checkboxes<br /><br />1= Delete Button Only<br />2= Checkbox Only<br />3= Both Delete Button and Checkbox' WHERE configuration_key = 'SHOW_SHOPPING_CART_DELETE';
UPDATE configuration SET configuration_title = 'Show Shopping Cart - Update Cart Button Location', configuration_description = 'Show on Shopping Cart Update Cart Button Location as:<br /><br />1= Next to each Qty Box<br />2= Below all Products<br />3= Both Next to each Qty Box and Below all Products' WHERE configuration_key = 'SHOW_SHOPPING_CART_UPDATE';
UPDATE configuration SET configuration_title = 'Show New Products on empty Shopping Cart Page', configuration_description = 'Show New Products on empty Shopping Cart Page<br />0= off or set the sort order' WHERE configuration_key = 'SHOW_SHOPPING_CART_EMPTY_NEW_PRODUCTS';
UPDATE configuration SET configuration_title = 'Show Featured Products on empty Shopping Cart Page', configuration_description = 'Show Featured Products on empty Shopping Cart Page<br />0= off or set the sort order' WHERE configuration_key = 'SHOW_SHOPPING_CART_EMPTY_FEATURED_PRODUCTS';
UPDATE configuration SET configuration_title = 'Show Special Products on empty Shopping Cart Page', configuration_description = 'Show Special Products on empty Shopping Cart Page<br />0= off or set the sort order' WHERE configuration_key = 'SHOW_SHOPPING_CART_EMPTY_SPECIALS_PRODUCTS';
UPDATE configuration SET configuration_title = 'Show Upcoming Products on empty Shopping Cart Page', configuration_description = 'Show Upcoming Products on empty Shopping Cart Page<br />0= off or set the sort order' WHERE configuration_key = 'SHOW_SHOPPING_CART_EMPTY_UPCOMING';
UPDATE configuration SET configuration_title = 'Show Notice of Combining Shopping Cart on Login', configuration_description = 'When a customer logs in and has a previously stored shopping cart, the products are combined with the existing shopping cart.<br /><br />Do you wish to display a Notice to the customer?<br /><br />0= OFF, do not display a notice<br />1= Yes show notice and go to shopping cart<br />2= Yes show notice, but do not go to shopping cart' WHERE configuration_key = 'SHOW_SHOPPING_CART_COMBINED';
UPDATE configuration SET configuration_title = 'Log Database Queries', configuration_description = 'Record the database queries to files in the system /logs/ folder. USE WITH CAUTION. This can seriously degrade your site performance and blow out your disk space storage quotas.<br><strong>Enabling this makes your site NON-COMPLIANT with PCI DSS rules, thus invalidating any certification.</strong>' WHERE configuration_key = 'STORE_DB_TRANSACTIONS';
UPDATE configuration SET configuration_title = 'Report All Errors (Admin)?', configuration_description = 'Do you want create debug-log files for <b>all</b> PHP errors, even warnings, that occur during your Zen Cart admin\'s processing?  If you want to log all PHP errors <b>except</b> duplicate-language definitions, choose <em>IgnoreDups</em>.' WHERE configuration_key = 'REPORT_ALL_ERRORS_ADMIN';
UPDATE configuration SET configuration_title = 'Report All Errors (Store)?', configuration_description = 'Do you want create debug-log files for <b>all</b> PHP errors, even warnings, that occur during your Zen Cart store\'s processing?  If you want to log all PHP errors <b>except</b> duplicate-language definitions, choose <em>IgnoreDups</em>.<br /><br /><strong>Note:</strong> Choosing \'Yes\' is not suggested for a <em>live</em> store, since it will reduce performance significantly!' WHERE configuration_key = 'REPORT_ALL_ERRORS_STORE';
UPDATE configuration SET configuration_title = 'Report All Errors: Backtrace on Notice Errors?', configuration_description = 'Include backtrace information on &quot;Notice&quot; errors?  These are usually isolated to the identified file and the backtrace information just fills the logs. Default (<b>No</b>).' WHERE configuration_key = 'REPORT_ALL_ERRORS_NOTICE_BACKTRACE';
UPDATE configuration SET configuration_title = 'E-Mail Transport Method', configuration_description = 'Defines the method for sending mail.<br><br><strong>PHP</strong> is the default, and uses built-in PHP wrappers for processing.<br><strong>smtpauth</strong> should be used by most sites, as it provides secure sending of authenticated email. You must also configure your smtpauth settings in the appropriate fields in this admin section.<br><strong>Gmail</strong> is used for sending emails using the Google mail service, and requires the [less secure] setting enabled in your gmail account.<br><strong>sendmail</strong> is for linux/unix hosts using the sendmail program on the server<br><strong>sendmail-f</strong> is only for servers which require the use of the -f parameter to use sendmail. This is a security setting often used to prevent spoofing. Will cause errors if your host mailserver is not configured to use it.<br><strong>Qmail</strong> is used for linux/unix hosts running Qmail as sendmail wrapper at /var/qmail/bin/sendmail.<br><br>MOST SITES WILL USE [smtpauth].' WHERE configuration_key = 'EMAIL_TRANSPORT';
UPDATE configuration SET configuration_title = 'SMTP Email Account Mailbox', configuration_description = 'Enter the mailbox account name (me@mydomain.com) supplied by your host. This is the account name that your host requires for SMTP authentication.<br />Only required if using SMTP Authentication for email.' WHERE configuration_key = 'EMAIL_SMTPAUTH_MAILBOX';
UPDATE configuration SET configuration_title = 'SMTP Email Account Password', configuration_description = 'Enter the password for your SMTP mailbox. <br />Only required if using SMTP Authentication for email.' WHERE configuration_key = 'EMAIL_SMTPAUTH_PASSWORD';
UPDATE configuration SET configuration_title = 'SMTP Email Mail Host', configuration_description = 'Enter the DNS name of your SMTP mail server.<br />ie: mail.mydomain.com<br />or 55.66.77.88<br />Only required if using SMTP Authentication for email.' WHERE configuration_key = 'EMAIL_SMTPAUTH_MAIL_SERVER';
UPDATE configuration SET configuration_title = 'SMTP Email Mail Server Port', configuration_description = 'Enter the IP port number that your SMTP mailserver operates on.<br />Only required if using SMTP Authentication for email.<br><br>Default: 25<br>Typical values are:<br>25 - normal unencrypted SMTP<br>587 - encrypted SMTP<br>465 - older MS SMTP port' WHERE configuration_key = 'EMAIL_SMTPAUTH_MAIL_SERVER_PORT';
UPDATE configuration SET configuration_title = 'Newsletter SMTP Email Account Mailbox', configuration_description = 'Enter the newsletter mailbox account name (me@mydomain.com) supplied by your host. This is the account name that your newsletter host requires for SMTP authentication.' WHERE configuration_key = 'NEWSLETTER_EMAIL_SMTPAUTH_MAILBOX';
UPDATE configuration SET configuration_title = 'Newsletter SMTP Email Account Password', configuration_description = 'Enter the password for your newsletter SMTP mailbox.' WHERE configuration_key = 'NEWSLETTER_EMAIL_SMTPAUTH_PASSWORD';
UPDATE configuration SET configuration_title = 'Newsletter SMTP Email Mail Host', configuration_description = 'Enter the DNS name of your Newsletter SMTP mail server if you are using a separate email server for bulk email.' WHERE configuration_key = 'NEWSLETTER_EMAIL_SMTPAUTH_MAIL_SERVER';
UPDATE configuration SET configuration_title = 'Newsletter SMTP Email Mail Server Port', configuration_description = 'Enter the IP port number that your newsletter SMTP mailserver operates on.<br><br>Default: 587<br>Typical values are:<br>587 - encrypted SMTP<br>465 - older MS SMTP port' WHERE configuration_key = 'NEWSLETTER_EMAIL_SMTPAUTH_MAIL_SERVER_PORT';
UPDATE configuration SET configuration_title = 'Newsletter Modules', configuration_description = 'Enter a comma-separated list of the modules that should use the newsletter settings when sending email (rather than the regular email settings).' WHERE configuration_key = 'NEWSLETTER_MODULES';
